import React from 'react'
import { Link, useHistory, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux'
import './Login.css'
import { useEffect } from 'react'
import login from '../helpers/auth';
import { updateAuthToken,updateUser } from '../redux/AuthContext';
import jwt_decode from "jwt-decode"
import { getLocal } from "../helpers/auth";
import { Toaster } from 'react-hot-toast';

const Login = () => {

    const history = useNavigate()
    const {user,authToken} = useSelector((state) => state.auth);
    const dispatch= useDispatch();
    const response = getLocal();
    
    useEffect(()=>{
      console.log(response);
      if (response) {
        history('/')
      }
    })
  return (
    <div>

<div>
         <div class="registration-form">
         <Toaster position='top-center' reverseOrder='false' ></Toaster>
        <form oonSubmit={handleSubmit}>
            <div class="form-icon">
                <span><i class="icon icon-user"></i></span>
            </div>
            <div class="form-group">
                <input type="text" class="form-control item" id="username" placeholder="Username"
                 onChange={e => setUsername(e.target.value)}/>
            </div>
            <div class="form-group">
                <input type="text" class="form-control item" id="email" placeholder="Email"
                 onChange={(e) => setEmail(e.target.value)}/>
            </div>
            <div class="form-group">
                <input type="password" class="form-control item" id="password" placeholder="Password"
                onChange={e => setPassword(e.target.value)}/>
            </div>
            <div class="form-group">
                <button type="button" class="btn btn-block create-account">Create Account</button>
            </div>
            <div className='signup-navi'>
            <p>Already a member..?</p>
            <p><Link className='lo-sign' to='/login'>Login</Link></p>
          </div>
        </form>
    </div>
    </div>
    
    </div>
  )
}

export default Login
