import React,{useEffect,useState} from 'react'
import './UserProfile.css'
import axios from 'axios'

import { getLocal } from '../helpers/auth'
import jwt_decode from "jwt-decode"
import { useNavigate } from 'react-router-dom'



function UserProfile() {

    
    const {user_id} = jwt_decode(getLocal())
    const [profile_img, setImage] = useState()
    const [password, setPassword] = useState()

    const history = useNavigate()


    const [user, setUser] = useState({
        username: '',
        email: "",
        profile_img: ""
    })
    useEffect(()=>{
        async function getUser(){
            
            const user = await axios.get(`http://localhost:2000/api/user-update/${user_id}/`)
            setUser(user.data)
            
        }
        getUser();
        
    },[])


    const updateProfile = async (e)=> {
        e.preventDefault()

        const response = await fetch(`http://localhost:2000/api/user-update-form/${user_id}/`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                profile_img,
            })
        })
        console.log('image',profile_img, 'user',user.username);
    }
    return (
        <div>

            <section class="vh-100" style={{backgroundColor: '#f4f5f7'}}>
                <div class="container my-4 py-5 h-100">
                    <div class="row d-flex justify-content-center align-items-center h-100">
                        <div class="col col-lg-6 mb-4 mb-lg-0">
                            <div class="card mb-3" style={{borderradius: '.5rem'}}>
                                <div class="row g-0">
                                    <div class="col-md-4 gradient-custom text-center text-white"
                                        style={{borderTopLeftRadius: '.5rem; border-bottom-left-radius: .5rem'}}>
                                        <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp"
                                            alt="Avatar" class="img-fluid my-5" style={{width:' 80px'}} />
                                        <h5>Marie Horwitz</h5>
                                        <p>Web Designer</p>
                                        <i class="far fa-edit mb-5"></i>
                                    </div>
                                    <div class="col-md-8">
                                        <div class="card-body p-4">
                                            <h6>Information</h6>
                                            <hr class="mt-0 mb-4"/>
                                                <div class="row pt-1">
                                                    <div class="col-6 mb-3">
                                                        <h6>Email</h6>
                                                        <p class="text-muted">info@example.com</p>
                                                    </div>
                                                    <div class="col-6 mb-3">
                                                        <h6>Phone</h6>
                                                        <p class="text-muted">123 456 789</p>
                                                    </div>
                                                </div>
                                                </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
            </section>


        </div>
    )
}
export default UserProfile
