import React, { useEffect, useState,useParams } from 'react'
import axios from 'axios'


import { toast } from 'react-hot-toast'
import { Link, useNavigate, } from 'react-router-dom'
import { Toaster } from 'react-hot-toast';

import Swal from 'sweetalert2'
import './AdminSidebar.css'




function AdminPanel() {
    const [userList, setUserList] = useState([])
    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const history = useNavigate()

    const id = useParams();
    const [singleUser, setUser] = useState({
        username: '',
        email: ''
    })

    useEffect(() => {

        async function getUserList() {
            const response = await axios.get('http://localhost:2000/api/class-userlist/')
            setUserList(response.data)
        }
        getUserList();

    }, [])
    const userUpdateForm = async (e) => {
        e.preventDefault()

        const response = await fetch(`http://localhost:2000/api/user-update/${id.user_id}/`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                username,
                email,
                password
            })
        }).then(history('/'))


    }

    const handleDelete = (id) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                const user = axios.delete(`http://localhost:2000/api/user-delete/${id}/`).then(
                    async function getUserList() {
                        const request = await axios.get('http://localhost:2000/api/user-list/')

                        setUserList(request.data)
                    }
                )

            }
        })
    }

    async function serachUser(keyword) {
        const request = await axios.get(`http://localhost:2000/api/class-userlist/?search=${keyword}`)
        console.log(request.data);
        if (request.data.length === 0) {

        }
        setUserList(request.data)
    }

    return (
        <div>
            <div class="admin-container">
                <div class="admin-sidebar">
                    <div class="sidebar-header">
                        <h3>Admin Panel</h3>
                    </div>
                    <ul class="sidebar-menu">
                        <li><Link to='add-user'>Add User</Link></li>

                    </ul>
                </div>
                <div class="admin-content">
                    <div class="admin-table">
                        <Toaster position='top-center' reverseOrder='false' ></Toaster>
                        <div class="search-bar">
                            <input onChange={e => serachUser(e.target.value)} type="search" class="search-input" id="datatable-search-input" placeholder="Search..." />
                            <button class="search-button" type="submit">
                                <i class="fa fa-search"></i>
                            </button>
                        </div>
                        <table class="table table-bordered my-4 " >
                            <caption></caption>
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Username</th>
                                    <th>Email</th>
                                    <th className='action-col'>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {userList.map((user) => (
                                    <tr>
                                        <td>{user.id}</td>
                                        <td>{user.username}</td>
                                        <td>{user.email}</td>
                                        <td><button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
                                            <i class="fas fa-edit"></i>
                                        </button>
                                            <p className='delete  mx-4' onClick={() => handleDelete(user.id)}> <i class="fa fa-trash" aria-hidden="true"></i></p></td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div >
            </div >
            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                <form className='add-user-form' onSubmit={userUpdateForm}>
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <div className='form-contain' >
                                
                                    <h2>Update User Here</h2>
                                    <input className='add-user-input' type="text" name='username' placeholder='username' defaultValue={singleUser.username}
                                        onChange={e => setUsername(e.target.value)}
                                    />
                                    {/* value={singleUser.username} value={singleUser.email} */}
                                    <input className='add-user-input' type="email" name='email' placeholder='email' defaultValue={singleUser.email}
                                        onChange={e => setEmail(e.target.value)}
                                    />
                                    <input className='add-user-input' type="password" name='password' placeholder='password'
                                        onChange={e => setPassword(e.target.value)}
                                    />
                                    <input className='add-user-button' type="submit" value='Update' />
                               
                            </div>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                    </form>
                </div>
            </div>
        </div >







    )
}

export default AdminPanel
