import React, { useEffect, useState } from 'react'
import axios from 'axios'

import { toast } from 'react-hot-toast'
import { Link, useNavigate } from 'react-router-dom'
import { Toaster } from 'react-hot-toast';
import { FaEdit } from 'react-icons/fa'
import { MdDeleteForever } from 'react-icons/md'
import Swal from 'sweetalert2'



function AdminPanel() {
    const [userList, setUserList] = useState([])

    useEffect(() => {

        async function getUserList() {
            const response = await axios.get('http://localhost:2000/api/class-userlist/')
            setUserList(response.data)
        }
        getUserList();

    }, [])

    const handleDelete = (id) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                const user = axios.delete(`http://localhost:2000/api/user-delete/${id}/`).then(
                    async function getUserList() {
                        const request = await axios.get('http://localhost:2000/api/user-list/')

                        setUserList(request.data)
                    }
                )

            }
        })
    }

    async function serachUser(keyword) {
        const request = await axios.get(`http://localhost:2000/api/class-userlist/?search=${keyword}`)
        console.log(request.data);
        if (request.data.length === 0) {
            toast.error('No users found')
        }
        setUserList(request.data)
    }

    return (
        <div>
            <Toaster position='top-center' reverseOrder='false' ></Toaster>
            <div class="form-outline mb-4">
                <input type="search" class="form-control" id="datatable-search-input" />
                <label class="form-label" for="datatable-search-input">Search</label>
            </div>
            <table class="table table-">
                <caption>List of users</caption>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th className='action-col'>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {userList.map((user) => (
                        <tr>
                            <td>{user.id}</td>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                            <td className='action-col' style={{ display: "flex" }}><Link className='action-text' to={`/update-user/` + user.id}><p className='edit'><FaEdit /> Edit</p></Link>
                                <p className='delete' onClick={() => handleDelete(user.id)}> Delete</p></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>


    )
}

export default AdminPanel
